CREATE TABLE Customers(Id INT NOT NULL PRIMARY KEY, Last VARCHAR(10), First VARCHAR(10));
CREATE TABLE Goods(Id INT NOT NULL PRIMARY KEY, Flavor VARCHAR(10), Food VARCHAR(10), Price FLOAT);
CREATE TABLE Items(Receipt INT NOT NULL PRIMARY KEY, Ordinal INT NOT NULL, Item VARCHAR(20), FOREIGN KEY(Receipt) REFERENCES Receipts(ReceiptNumber));
CREATE TABLE Receipts(ReceiptNumber INT NOT NULL PRIMARY KEY, Date DATE, Customer INT NOT NULL, FOREIGN KEY(Customer) REFERENCES Customers(Id));