CREATE TABLE Continents(ContId INT NOT NULL PRIMARY KEY, Name VARCHAR(10) NOT NULL);
CREATE TABLE Countries(CountryId INT NOT NULL PRIMARY KEY, Name VARCHAR(15) NOT NULL, Continent VARCHAR(15) NOT NULL, FOREIGN KEY(Continent) REFERENCES Continents(ContId));
CREATE TABLE CarMakers(Id INT NOT NULL PRIMARY KEY, Short VARCHAR(15) NOT NULL, Name VARCHAR(25) NOT NULL, Country INT NOT NULL, FOREIGN KEY(Country) REFERENCES Countries(CountryId));
CREATE TABLE Models(ModelId INT NOT NULL PRIMARY KEY, Maker INT ID NOT NULL, Model VARCHAR(15) NOT NULL, FOREIGN KEY(Maker) REFERENCES CarMakers(Id), UNIQUE(Model));
CREATE TABLE CarNames(MakeId INT NOT NULL PRIMARY KEY, Model VARCHAR(15) NOT NULL, Description VARCHAR(50) NOT NULL, FOREIGN KEY(Model) REFERENCES Models(Model));
CREATE TABLE CarsData(Id INT NOT NULL PRIMARY KEY, Mpg INT NOT NULL, Cylinders INT NOT NULL, Edispl INT NOT NULL, Horsepower INT NOT NULL, Weight INT NOT NULL, Accelerate FLOAT NOT NULL, Year INT NOT NULL, FOREIGN KEY(Id) REFERENCES CarNames(MakeId));
