CREATE TABLE Appelations(Id INT NOT NULL, Appelation VARCHAR(30) PRIMARY KEY, County VARCHAR(20), State VARCHAR(15), Area VARCHAR(20), isAVA VARCHAR(5));
CREATE TABLE Grapes(Id INT NOT NULL, Grape VARCHAR(25) PRIMARY KEY, Color VARCHAR(10), UNIQUE(Id));
CREATE TABLE Wine(Id INT NOT NULl PRIMARY KEY, Grape VARCHAR(25), Winery VARCHAR(20), Appelation VARCHAR(30), Name VARCHAR(30), Year INT, Price INT, Score INT, Cases INT, Drink VARCHAR(7), FOREIGN KEY(Grape) REFERENCES Grapes(Grape), FOREIGN KEY(Appelation) REFERENCES Appelations(Appelation));